mapper.select:
SELECT content_id, content_type_id, title, addr1, addr2, zipcode, tel, first_image, first_image2, readcount, sido_code, gugun_code, latitude, longitude, mlevel FROM attraction_info
AND sido_code=#{sidoCode}
AND content_type_id=#{contentTypeId}
AND title LIKE concat('%', #{title}, '%')
ORDER BY content_id LIMIT 300

SELECT gugun_code, gugun_name, sido_code
FROM gugun
WHERE sido_code = #{sidoCode}

SELECT overview
FROM attraction_description
WHERE content_id=#{contentId}
