mapper.select:
SELECT member_id, username, id, nickname, email
FROM members
WHERE id = #{id};

SELECT *
FROM members
order by member_id;

mapper.update:
UPDATE members
SET password = #{password}, nickname = #{nickname}, email = #{email}
where member_id = #{memberId}

mapper.select:
select count(memberId)
from members
where member_id = #{memberId}

mapper.update:
update members
set token = #{token}
where member_id = #{memberId}

mapper.select:
select token
from members
where member_id = #{memberId}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where member_id = #{memberId}
